import React from 'react';

export default function Input(){
    return(
        <main>
            <h1 className = "main-title">Input</h1>
            <ul className = "main-container">
                <h3>This questionairre is designed to be completed alongside a clinician. For each of the following, select the scale that best represents the patient:</h3>
                <Question 
                index = "1" 
                question = "Depressed Mood (sadness, hopeless, helpless, worthless)"
                answers = {["These feeling states indicated only on questioning.", "These feeling states spontaneously reported verbally.", "Communicates feeling states non-verbally, i.e. through facial expression, posture, voice, and tendency to weep.", "Patient reports virtually only these feeling states in his/her spontaneous verbal and non-verbal communication."]}/>
            </ul>
        </main>
    )
}

function Question(props)
{
    return(
        <div>
            <h2>{props.index}. {props.question}</h2>
            <ul>
                
                <InputButton answers = {props.answers}/>
            </ul>
        </div>
    )
}

function InputButton(props){
    buttons = props.answers.map()
    
    return(
        <div className = "button-container">
            <button>0</button>
            <h4>These feeling states indicated only on questioning.</h4>
        </div>
    )
}
